const verCalendario = () => {
  const data = new Date(Date.now());
  const mes = ("0" + (data.getMonth() + 1)).slice(-2);
  const ano = data.getFullYear();
  let dia = Number(data.getDay()) + 2;
  dia = ("0" + dia.toString()).slice(-2);
  return `${ano}/${mes}/${dia}`;
};

