import React, { useEffect } from "react";
import Dashboard from "./dashboard/dashboard";
import Login from "./LoginPage/login";


export default function VerificaLogin() {

  useEffect(() => { 
    const token = localStorage.getItem('token')

    switch (token){
    case (token === null):
      return <Login />;
    case (token !== null):
      return <Dashboard />
    }

  }, []);
}

