import React, { useEffect } from "react";
import Dashboard from "./dashboard/dashboard";
import Login from "./LoginPage/login";


export default function VerificaLogin() {

  useEffect(() => { 
    const [mudarPagina, setMudarPagina] = useState('')

    const telaLogin = () => {
      setMudarPagina('telaLogin');
    }
    const telaDashboard = () => {
      setMudarPagina('telaDashboard');
    }
    const token = localStorage.getItem('token')

    if (token === null) {
      return <Login telaLogin={telaLogin} />;
    }
    else {
      return <Dashboard telaDashboard={telaDashboard} />
    }

  }, []);
}
