import styled from "styled-components";
import Fundo3 from '../../components/img/fundo7.jpg'

export const Fundo = styled.div`
  background: url(${Fundo3}) no-repeat right;
  width: 100%;
	height: 100vh;
	background-size: cover;
	text-align: center;
	padding: 5% 0;
  overflow-x: hidden;
	@media (max-width: 700px) {
		background-size: auto;
	}
`
export const Form = styled.form`
	display: flex;
	flex-direction: column;
	width: 30%;
	margin: 3% auto;
`
export const Tittle = styled.h1`
	background: rgba(0, 0, 0, 0.5);
	width: 50%;
	color: white;
	font-size: 32px;
	font-weight: bold;
	margin: 2%;
	font-family: 'Helvetica', 'Sans-Bold';
	@media (min-width: 1600px) {
		margin: 2%;
		font-size: 32px;
	}
	@media (max-width: 700px) {
		width: 97%;
		margin: 10% 0;
	}
`;


export const Button = styled.button`
	background: linear-gradient(90deg, rgba(146, 49, 170, 1) 2%, rgba(61, 153, 205, 1) 100%);
	color: white;
	cursor: pointer;
	font-size: 20px;
	font-weight: bolder;
	width: 280px;
	margin: 10px;
	border-radius: 350px;
	height: 50px;
	&:hover {
		background: linear-gradient(90deg, #471254 40%, #71c5f5 100%);
		border: solid 2px black;
	}
	@media (min-width: 1600px) {
		margin: 5%;
		width: 500px;
		height: 70px;
		font-size: 32px;
	}
	@media (max-width: 700px) {
		margin: 10px 0;
		width: 350px;
		height: 50px;
		font-size: 22px;
		align-items: center;
		display: flex;
		justify-content: center;
	}
`;
